  PRIMARY KEY (`type_id`),
  KEY `FK_article_type_user` (`user_id`),
  CONSTRAINT `FK_article_type_user` FOREIGN KEY (`user_id`) REFERENCES `t_user` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8 COLLATE=utf8_bin;

